"css`
        :host {
            display: block;
            padding: 20px;
            max-width: 1200px;
            margin: 0 auto;
        }

        .controls {
            display: flex;
            gap: 10px;
            margin-bottom: 30px;
            align-items: center;
            flex-wrap: wrap;
        }

        .api-toggle {
            display: flex;
            gap: 10px;
            align-items: center;
        }

        .api-toggle label {
            font-weight: 500;
        }

        .api-toggle input[type="radio"] {
            margin-right: 5px;
        }

        .load-button {
            padding: 12px 24px;
            border: none;
            border-radius: 8px;
            background: linear-gradient(135deg, #ff6b35, #f7931e);
            color: white;
            font-weight: 600;
            cursor: pointer;
            transition: transform 0.2s ease, box-shadow 0.2s ease;
        }

        .load-button:hover {
            transform: translateY(-2px);
            box-shadow: 0 4px 12px rgba(255, 107, 53, 0.3);
        }

        .load-button:disabled {
            opacity: 0.6;
            cursor: not-allowed;
            transform: none;
        }

        .gallery {
            display: flex;
            flex-direction: column;
            gap: 0;
            margin-top: 20px;
            max-width: 600px;
            margin-left: auto;
            margin-right: auto;
        }

        .loading {
            text-align: center;
            padding: 40px;
            font-size: 18px;
            color: var(--text-secondary, #666);
        }

        .error {
            text-align: center;
            padding: 40px;
            color: #e74c3c;
            background: #ffeaea;
            border-radius: 8px;
            border-left: 4px solid #e74c3c;
        }

        .loading-spinner {
            width: 30px;
            height: 30px;
            border: 3px solid #f3f3f3;
            border-top: 3px solid #ff6b35;
            border-radius: 50%;
            animation: spin 1s linear infinite;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        /* Dark mode styles */
        :host([data-theme="dark"]) .error {
            background: #2a1f1f;
            color: #ff6b6b;
        }

        :host([data-theme="dark"]) .loading-spinner {
            border: 3px solid #444;
            border-top: 3px solid #ff6b35;
        }

        /* Mobile responsiveness */
        @media (max-width: 768px) {
            :host {
                padding: 10px;
            }
            
            .gallery {
                max-width: 100%;
            }
            
            .controls {
                flex-direction: column;
                align-items: stretch;
                margin-bottom: 15px;
            }
            
            .api-toggle {
                justify-content: center;
            }
        }
    `"

"css`
        :host {
            display: block;
            background: white;
            border: 1px solid #dbdbdb;
            margin-bottom: 20px;
            width: 100%;
            max-width: 600px;
            min-height: 80vh;
            transition: all 0.2s ease;
        }

        :host(:last-child) {
            margin-bottom: 60px;
        }

        .card-header {
            display: flex;
            align-items: center;
            padding: 16px;
            gap: 12px;
        }

        .author-avatar {
            width: 40px;
            height: 40px;
            border-radius: 50%;
            object-fit: cover;
            border: 2px solid #e0e0e0;
        }

        .author-info {
            flex: 1;
        }

        .author-name {
            font-weight: 600;
            color: #262626;
            margin: 0;
            font-size: 14px;
        }

        .author-details {
            color: #8e8e8e;
            font-size: 12px;
            margin: 2px 0 0 0;
        }

        .photo-container {
            position: relative;
            width: 100%;
            height: 60vh;
            min-height: 400px;
            max-height: 600px;
            overflow: hidden;
            background: #f8f8f8;
        }

        .photo {
            width: 100%;
            height: 100%;
            display: block;
            object-fit: cover;
            transition: opacity 0.3s ease;
        }

        .photo[data-src] {
            opacity: 0;
        }

        .photo:not([data-src]) {
            opacity: 1;
        }

        .photo-placeholder {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(45deg, #f0f0f0 25%, transparent 25%, transparent 75%, #f0f0f0 75%, #f0f0f0),
                        linear-gradient(45deg, #f0f0f0 25%, transparent 25%, transparent 75%, #f0f0f0 75%, #f0f0f0);
            background-size: 20px 20px;
            background-position: 0 0, 10px 10px;
            display: flex;
            align-items: center;
            justify-content: center;
            color: #999;
            font-size: 14px;
            z-index: 1;
        }

        .loading-spinner {
            width: 40px;
            height: 40px;
            border: 3px solid #f3f3f3;
            border-top: 3px solid #3498db;
            border-radius: 50%;
            animation: spin 1s linear infinite;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        .card-actions {
            padding: 12px 16px;
        }

        .action-buttons {
            display: flex;
            gap: 16px;
            margin-bottom: 8px;
        }

        .action-button {
            border: none;
            background: none;
            cursor: pointer;
            font-size: 20px;
            padding: 4px;
            border-radius: 4px;
            transition: background-color 0.2s ease, transform 0.1s ease;
            display: flex;
            align-items: center;
            gap: 4px;
        }

        .action-button:hover {
            background-color: #f5f5f5;
            transform: scale(1.1);
        }

        .action-button:active {
            transform: scale(0.95);
        }

        .like-button.liked {
            color: #e74c3c;
        }

        .dislike-button.disliked {
            color: #8e44ad;
        }

        .share-button {
            color: #3498db;
        }

        .engagement {
            color: #262626;
            font-size: 14px;
            font-weight: 600;
            margin-bottom: 8px;
        }

        .photo-title {
            color: #262626;
            font-size: 14px;
            margin: 0;
            word-wrap: break-word;
        }

        .photo-date {
            color: #8e8e8e;
            font-size: 12px;
            margin-top: 4px;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }

        /* Dark mode styles */
        :host([data-theme="dark"]) {
            background: #000000;
            border-color: #262626;
            color: #ffffff;
        }

        :host([data-theme="dark"]) .photo-container {
            background: #262626;
        }

        :host([data-theme="dark"]) .author-name,
        :host([data-theme="dark"]) .engagement,
        :host([data-theme="dark"]) .photo-title {
            color: #ffffff;
        }

        :host([data-theme="dark"]) .author-details,
        :host([data-theme="dark"]) .photo-date {
            color: #a0a0a0;
        }

        :host([data-theme="dark"]) .action-button:hover {
            background-color: #404040;
        }

        :host([data-theme="dark"]) .photo-placeholder {
            background: linear-gradient(45deg, #404040 25%, transparent 25%, transparent 75%, #404040 75%, #404040),
                        linear-gradient(45deg, #404040 25%, transparent 25%, transparent 75%, #404040 75%, #404040);
            background-size: 20px 20px;
            background-position: 0 0, 10px 10px;
            color: #888;
        }

        /* Mobile responsiveness */
        @media (max-width: 768px) {
            :host {
                min-height: 70vh;
                margin-bottom: 15px;
            }

            .photo-container {
                height: 50vh;
                min-height: 300px;
            }

            .card-header {
                padding: 12px;
            }

            .card-actions {
                padding: 8px 12px;
            }

            .action-buttons {
                gap: 12px;
            }

            .action-button {
                font-size: 18px;
            }

            .author-name {
                font-size: 13px;
            }

            .author-details {
                font-size: 11px;
            }
        }

        @media (max-width: 480px) {
            :host {
                min-height: 65vh;
            }

            .photo-container {
                height: 45vh;
                min-height: 250px;
            }
        }
    `"
